package com.openclassrooms.paymybuddy.model;

import javax.persistence.*;

@Entity
@Table(name = "role_profile")
public class RoleProfile {
    /**
     * ID, generated by DataBase.
     * Use as primary key in DataBase.
     */
    @Id
    @GeneratedValue(strategy = GenerationType.AUTO)
    @Column(name = "role_id")
    private int id;

    /**
     * Role name defined by RoleType enum.
     * @see RoleType
     */
    @Column(name = "role_name")
    private RoleType name;

    /**
     * Public constructor.
     * @param name .
     */
    public RoleProfile(RoleType name) {
        this.name = name;
    }

    /**
     * Getter ID.
     * @return ID
     */
    public int getId() {
        return id;
    }

    /**
     * Setter ID.
     * ID is auto-generated, should not be accessible.
     * @param pId to set
     */
    private void setId(final int pId) {
        id = pId;
    }

    public RoleType getName() {
        return name;
    }

    public void setName(RoleType pName) {
        name = pName;
    }
}
